import React, { useState } from 'react'
import { AppBar, Box, Button, Tab, Tabs, Toolbar, Typography } from '@mui/material'
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';

const Header = () => {
  const isLoggedIn = useSelector(state => state.isLoggedIn)
  const [value, setValue] = useState();
  return (
    <AppBar position='sticky' >
      <Toolbar>
        <Typography variant='h4'>
          BlogApp
        </Typography>
        {
          isLoggedIn && 
          <Box display="flex" marginLeft="auto" marginRight="auto">
          <Tabs textColor='inherit' value={value} onChange={(e,val) => setValue(val)} >
            <Tab LinkComponent={Link} to="/blogs"  label="All Blogs" />
            <Tab LinkComponent={Link} to="/myBlogs" label="My Blog" />
          </Tabs>
        </Box>
        }
        
        <Box variant='h4' display="flex" marginLeft="auto">
          {!isLoggedIn && 
          <>
            <Button LinkComponent={Link} to="/auth" variant='contained' sx={{margin: 1, borderRadius: 10}} color='warning'>Login</Button>
            <Button LinkComponent={Link} to="/myBlogs" variant='contained' sx={{margin: 1, borderRadius: 10}} color='warning'>Signup</Button>
          </>
          }
          
          { 
          isLoggedIn &&
          <Button LinkComponent={Link} to="/myBlogs" variant='contained' sx={{margin: 1, borderRadius: 10}} color='warning'>LogOut</Button>
          }
          
        </Box>
      </Toolbar>
    </AppBar>
  )
}

export default Header
