import { Box, Button, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'


const Auth = () => {
  const [isSignup, setIsSignup] = useState(false);
  return (
    <>
      <form>
        <Box display='flex' flexDirection='column' justifyContent='center' alignItems='center' boxShadow= "10px 10px 20px #ccc" padding={3} borderRadius={5} marginTop={5} margin="auto">
          <Typography padding={3} textAlign='center' variant='h4'> {isSignup ? "Signup" : "Login"} </Typography>
          
          {isSignup && <TextField placeholder='Name' margin="normal" />}
          
          <TextField type={"email"} placeholder='Email' margin="normal" />
          <TextField type={"password"} placeholder='Password' margin="normal" />
          <Button varient="contained" sx={{ borderRadius: 3, marginTop: 3}} color='warning' >Submit</Button>
          <Button sx={{ borderRadius: 3, marginTop: 3}} onClick={() => setIsSignup(!isSignup)} >Change to {isSignup ? "Login" : "Signup"}</Button>
        </Box>
      </form>
    </>
  )
}

export default Auth
